import Utils from"./Utils";import SimpleReactValidator from"simple-react-validator";let customValid={className:"text-danger",validators:{empty:{message:"The :attribute must be a valid value.",rule:function(a){return!Utils.isEmpty(a)}},each:{message:"The :attribute must be a valid value.",rule:function(val,options){if(0==val.length)return!0;let v=val.filter(function(r){return eval(options[0]+"("+window.CAP.Utils.toJSON(r)+")")});return 0<v.length}},date:{message:"The :attribute must be a valid value.",rule:function(a){let b=Date.parse(a);return isNaN(b)?null:new Date(b)}},alpha_num:{message:"The :attribute may only contain letters and numbers.",rule:function(a,b,c){return c.helpers.testRegex(a,/^[A-Za-z0-9]*$/)}},password:{message:"The :attribute must be a valid value.",rule:function(a,b,c){return!(""!=a)||c.helpers.testRegex(a,/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[$@$!%*?&])[A-Za-z\d$@$!%*?&]{8,10}/)}},boolean:{message:"The :attribute must be a valid value.",rule:function(a){return 0<=["true","false","1","0"].indexOf(a.toString())}},ip:{message:"The :attribute must be a valid IP address.",rule:function(a,b,c){return c.helpers.testRegex(a,/^(?!0)(?!.*\.$)((1?\d?\d|25[0-5]|2[0-4]\d)(\.|$)){4}$/i)&&-1===b.indexOf(a)}},ip2:{message:"The :attribute must be a valid IP address.",rule:function(a){var b=require("ip-subnet-calculator");return!(""!=a)||b.isIp(a)}},confrim:{message:":attribute veriler e\u015Fle\u015Fmiyor. ",rule:function(a,b){let c=document.querySelector("[name='"+b[0]+"']").value;return c==a}}}};const Validator=new SimpleReactValidator(customValid);export default Validator;